import React, { createContext, useState, useEffect } from 'react';
import api from '../api/api'; // Your configured Axios instance
import authApi from '../api/auth.api'; // Import the new authApi functions

// Create the context that components will consume
export const AuthContext = createContext(null);

/**
 * ==============================================================================
 * FILE: /client/src/context/AuthProvider.jsx
 * PURPOSE: Manages global authentication state.
 * (UPDATED to use real API calls instead of mock data)
 * ==============================================================================
 */
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // To handle initial auth check

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // On app startup, verify the token with the backend to get user data
      const verifyToken = async () => {
        try {
          // Set the token for the API call
          api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
          const response = await authApi.getCurrentUser();
          setUser(response.data);
        } catch (error) {
          console.error("Token verification failed:", error);
          localStorage.removeItem('token'); // Clear invalid token
        }
      };
      verifyToken();
    }
    setLoading(false);
  }, []);

  // --- Authentication Functions ---

  const login = async (email, password) => {
    try {
      // Make a real API call to the backend's login endpoint
      const response = await authApi.login({ email, password });
      
      const { token, ...userData } = response.data;

      // Store the token in local storage
      localStorage.setItem('token', token);
      
      // Set the Authorization header for all future Axios requests
      api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      
      // Update the user state
      setUser(userData);
      
      return userData;
    } catch (error) {
      console.error("Login failed:", error);
      // Here you would handle login errors (e.g., show a toast notification)
      throw error;
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    delete api.defaults.headers.common['Authorization'];
    setUser(null);
    // Redirect the user to the login page
    window.location.href = '/login';
  };

  // The value that will be available to all consuming components
  const value = {
    user,
    isAuthenticated: !!user,
    loading,
    login,
    logout,
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
