import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import AppLayout from '../components/layout/AppLayout';
import PrivateRoute from './PrivateRoute'; // Assuming PrivateRoute is created
import LoginPage from '../pages/auth/LoginPage';
import PersonalDashboardPage from '../pages/dashboard/PersonalDashboardPage';
import ProjectBoardPage from '../pages/project/ProjectBoardPage';
// Import other pages as needed

const AppRouter = () => {
  return (
    <Routes>
      <Route path="/login" element={<LoginPage />} />
      
      {/* All private routes are now children of the AppLayout route */}
      <Route 
        path="/" 
        element={
          <PrivateRoute>
            <AppLayout />
          </PrivateRoute>
        }
      >
        <Route index element={<Navigate to="/dashboard" replace />} />
        <Route path="dashboard" element={<PersonalDashboardPage />} />
        <Route path="project/:projectId" element={<ProjectBoardPage />} />
        {/* Add other nested routes for settings, portfolio, etc. here */}
      </Route>

      {/* Add NotFoundPage route here */}
    </Routes>
  );
};

export default AppRouter;
