import React from 'react';
import useAuth from '../../hooks/useAuth'; // Now used for the logout button

const Header = () => {
  const { user, logout } = useAuth();

  return (
    <header className="bg-white p-4 flex justify-between items-center border-b border-gray-200 shrink-0">
      <div className="relative w-full max-w-xs">
        {/* Search Input */}
      </div>
      <div className="flex items-center gap-4">
        {/* Icons */}
        <div className="flex items-center gap-3">
          <img className="w-10 h-10 rounded-full object-cover" src={`https://i.pravatar.cc/40?u=${user?.email}`} alt="User avatar" />
          <div className="hidden sm:block">
            <p className="font-semibold text-sm text-gray-800">{user?.name}</p>
            <p className="text-xs text-gray-500">{user?.email}</p>
          </div>
        </div>
        <button onClick={logout} className="text-sm text-gray-500 hover:text-primary">Logout</button>
      </div>
    </header>
  );
};

export default Header;
